DROP TABLE IF EXISTS `news`;
CREATE TABLE `news` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(200) NOT NULL,
  `content` text NOT NULL,
  `pub_time` varchar(50) NOT NULL COMMENT '来源站发布时间',
  `source` varchar(200) NOT NULL COMMENT '来源',
  `source_url` varchar(200) NOT NULL COMMENT '最初来源站',
  `origin_url` varchar(400) NOT NULL COMMENT '抓取来源url',
  `create_time` datetime NOT NULL COMMENT '抓取时间',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `stock`;
CREATE TABLE `stock` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `code` varchar(6) NOT NULL COMMENT '代码',
  `name` varchar(50) NOT NULL COMMENT '名字',
  `rating` float NOT NULL COMMENT '星级',
  `price` float NOT NULL COMMENT '买入指导价',
  `type` int(10) NOT NULL COMMENT '类型（短线，中线。。）',
  `topic` varchar(200) NOT NULL COMMENT '概念',
  `reason` text NOT NULL COMMENT '原因',
  `create_time` datetime NOT NULL,
  `author` varchar(50) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='股票信息';


DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `password` varchar(200) COLLATE utf8_unicode_ci NOT NULL,
  `email` varchar(200) COLLATE utf8_unicode_ci NOT NULL,
  `phone` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL,
  `nickname` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL,
  `status` int(3) NOT NULL DEFAULT '1' COMMENT '权限',
  `time` datetime NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;


DROP TABLE IF EXISTS `weixin`;
CREATE TABLE `weixin` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(200) NOT NULL,
  `intro` text NOT NULL,
  `img` varchar(200) NOT NULL,
  `create_time` datetime NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='微信公众号';


